mapper.select:
SELECT *
FROM shop.shop_address
WHERE user_id =#{userId} AND id =#{id}

mapper.update:
UPDATE shop.shop_address
SET is_default = 1
WHERE user_id =#{userId}

UPDATE shop.shop_address
SET is_default = 0
WHERE user_id =#{userId}

mapper.select:
