mapper.select:
SELECT *
FROM shop_goods
WHERE category_id =#{categoryId} AND deleted = 0;

SELECT *
FROM shop_goods
WHERE id =#{id} AND deleted = 0;

SELECT *
FROM shop_goods
WHERE name =#{name} AND deleted = 0;

SELECT *
FROM shop_goods
WHERE goods_id =#{goodsId} AND deleted = 0;

SELECT *
FROM shop_goods
WHERE goods_id =#{goodsId} AND name =#{name} AND deleted = 0;

mapper.update:
UPDATE shop_goods
SET shop_goods.deleted = 1
WHERE id =#{id}
