mapper.select:
SELECT *
FROM shop_goods
WHERE id =#{id} AND deleted = 0 AND shop_goods.is_on_sale = 1;

SELECT *
FROM shop_goods
WHERE name =#{name} AND deleted = 0 AND shop_goods.is_on_sale = 1;

SELECT *
FROM shop_goods
WHERE goods_id =#{goodsId} AND deleted = 0 AND shop_goods.is_on_sale = 1;

SELECT *
FROM shop_goods
WHERE goods_id =#{goodsId} AND name =#{name} AND deleted = 0 AND shop_goods.is_on_sale = 1;

mapper.update:
UPDATE shop_goods
SET shop_goods.deleted = 1
WHERE id =#{id}

UPDATE shop_goods
SET category_id=#{categoryId}, goods_Id=#{goodsId}, gallery=#{gallery}, name=#{name}, brief=#{brief},keywords=#{keywords},
    pic_url=#{picUrl}, unit=#{unit}, original_price=#{originalPrice}, retail_price=#{retailPrice}, create_date=#{createDate},
    update_by=#{updateBy}, update_date=#{updateDate}, remarks=#{remarks}, is_on_sale=#{isOnSale}, deleted=#{deleted}
WHERE id=#{id}
