mapper.update:
UPDATE shop_user
SET id=#{id},username=#{username},password=#{password},gender=#{gender},birthday=#{birthday},
    last_login_time=#{lastLoginTime} ,user_level=#{userLevel} ,nickname=#{nickname},mobile=#{mobile},
    avatar=#{avatar},wechat_openid=#{wechatOpenid}  ,session_key=#{sessionKey}  ,status=#{status},
    add_time=#{addTime}  ,update_time=#{updateTime}  ,deleted=#{deleted}
WHERE id=#{id}

UPDATE shop_user
SET password=#{password}
WHERE id=#{id}

UPDATE shop_user
SET last_login_time=#{lastLoginTime}
WHERE id=#{id}

mapper.select:
SELECT *
FROM shop_user
ORDER BY id=#{id};

mapper.update:
UPDATE shop_user SET
    shop_user.status = '1'
WHERE id = #{id}

UPDATE shop_user SET
    shop_user.status = '0'
WHERE id = #{id}
